'use strict';

$(document).ready(function () {
    $(window).on('scroll', function () {
        var mouseposit = $(window).scrollTop();
        var startAnimate = 1;
        if (mouseposit > startAnimate) {
            $('.arrow').addClass('animated');
        } else {
            $('.arrow').removeClass('animated');
        }
    });
});
$(document).ready(function () {
    var swiper = new Swiper('.swiper-container', {
        slidesPerView: 5,
        spaceBetween: 10,
        centeredSlides: true,
        pagination: {
            el: '.swiper-pagination',
            clickable: true
        }
    });

    function cu(className) {
        var index = 0;
        $.each($('.swiper-wrapper').children(), function (i, item) {
            if ($(item).hasClass(className)) {
                index = i;
                return false;
            }
        });
        return index;
    }
    swiper.slideTo(cu('careca', 0, !1));
});